# Initialize object references
plane = bpy.data.objects["plane"]
prop = bpy.data.objects["propeller"]
camC = bpy.data.objects["CameraC"]
camD = bpy.data.objects["CameraD"]
#c = bpy.data.objects["Cube"]0

# Declare function
def set_keyframes():
    frame_num = 0
    
    for index,position in enumerate(positions):
        bpy.context.scene.frame_set(frame_num)
        # Record location coords
        plane.location = position
        camC.location = cPositions[index]
        camD.location = dPositions[index]   
                  
        # Record rotation angles in XYZ Euler
        plane.rotation_euler = (math.radians(rotations[index][0]), math.radians(rotations[index][1]), math.radians(rotations[index][2]))
        camC.rotation_euler = (math.radians(cRotations[index][0]), math.radians(cRotations[index][1]), math.radians(cRotations[index][2]))
        camD.rotation_euler = (math.radians(dRotations[index][0]), math.radians(dRotations[index][1]), math.radians(dRotations[index][2]))
            
        # Record keyframes
        plane.keyframe_insert(data_path='location', index=-1)
        plane.keyframe_insert(data_path='rotation_euler', index=-1)
        camC.keyframe_insert(data_path='location', index=-1)
        camC.keyframe_insert(data_path='rotation_euler', index=-1)
        camD.keyframe_insert(data_path='location', index=-1)
        camD.keyframe_insert(data_path='rotation_euler', index=-1)

        frame_num += 40
    pass

def main():
    set_keyframes()
    
    pass
    
main()